import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/auth.ts.content:
import jwt from 'jsonwebtoken';
import bcrypt from 'bcrypt';
import crypto from 'crypto';
import { Request, Response, NextFunction } from 'express';
import { storage } from './storage';
import { User } from '@shared/schema';

const JWT_SECRET = process.env.SESSION_SECRET || 'your-secret-key';
const SALT_ROUNDS = 10;

export interface AuthenticatedRequest extends Request {
  user?: User;
}

export async function hashPassword(password: string): Promise<string> {
  return await bcrypt.hash(password, SALT_ROUNDS);
}

export async function verifyPassword(password: string, hash: string): Promise<boolean> {
  return await bcrypt.compare(password, hash);
}

export function generateToken(userId: string): string {
  return jwt.sign({ userId }, JWT_SECRET, { expiresIn: '24h' });
}

export function verifyToken(token: string): { userId: string } | null {
  try {
    return jwt.verify(token, JWT_SECRET) as { userId: string };
  } catch {
    return null;
  }
}

export function generateApiKey(): string {
  const randomBytes = crypto.randomBytes(32);
  return `sk_${randomBytes.toString('hex')}`;
}

export function hashApiKey(apiKey: string): string {
  return crypto.createHash('sha256').update(apiKey).digest('hex');
}

export async function authenticateToken(
  req: AuthenticatedRequest,
  res: Response,
  next: NextFunction
) {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({ message: 'Access token required' });
  }

  try {
    if (token.startsWith('sk_')) {
      const keyHash = hashApiKey(token);
      const apiKey = await storage.getApiKeyByHash(keyHash);

      if (!apiKey) {
        return res.status(403).json({ message: 'Invalid API key' });
      }

      const user = await storage.getUser(apiKey.userId);
      if (!user || !user.isActive) {
        return res.status(403).json({ message: 'User not found or inactive' });
      }

      await storage.updateApiKeyLastUsed(apiKey.id);
      req.user = user;
      next();
    } else {
      const decoded = verifyToken(token);
      if (!decoded) {
        return res.status(403).json({ message: 'Invalid token' });
      }

      const user = await storage.getUser(decoded.userId);
      if (!user || !user.isActive) {
        return res.status(403).json({ message: 'User not found or inactive' });
      }

      req.user = user;
      next();
    }
  } catch (error) {
    return res.status(500).json({ message: 'Internal server error' });
  }
}

export function requireRole(roles: string[]) {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    if (!req.user) {
      return res.status(401).json({ message: 'Authentication required' });
    }

    if (!roles.includes(req.user.role)) {
      return res.status(403).json({ message: 'Insufficient permissions' });
    }

    next();
  };
}

server/db.ts.content:
import pkg from 'pg';
const { Pool } = pkg;
import { drizzle } from 'drizzle-orm/node-postgres';
import * as schema from "@shared/schema";

// Support both DATABASE_URL and individual PG* environment variables
// This provides flexibility for different deployment environments
let pool: InstanceType<typeof Pool>;

if (process.env.DATABASE_URL) {
  // Use DATABASE_URL if available (common in production)
  pool = new Pool({
    connectionString: process.env.DATABASE_URL,
    ssl: process.env.NODE_ENV === 'production' && process.env.DATABASE_URL.includes('sslmode=require')
      ? { rejectUnauthorized: false }
      : undefined
  });
} else if (process.env.PGHOST) {
  // Use individual PG* variables (common in Docker/VPS deployments)
  pool = new Pool({
    user: process.env.PGUSER,
    password: process.env.PGPASSWORD,
    database: process.env.PGDATABASE,
    host: process.env.PGHOST,
    port: parseInt(process.env.PGPORT || '5432', 10),
  });
} else {
  throw new Error(
    "Database configuration missing. Set either DATABASE_URL or PGHOST/PGUSER/PGPASSWORD/PGDATABASE environment variables."
  );
}

export { pool };
export const db = drizzle(pool, { schema });

