import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import * as cron from 'node-cron';
import { DatabaseStorage } from './storage';
import { log } from './logger';

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  // Only capture response bodies in development for debugging
  if (app.get("env") === "development") {
    const originalResJson = res.json;
    res.json = function (bodyJson, ...args) {
      capturedJsonResponse = bodyJson;
      return originalResJson.apply(res, [bodyJson, ...args]);
    };
  }

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      // In production, only log method, path, status, and duration (no response body for privacy)
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;

      // Only include response body in development
      if (app.get("env") === "development" && capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  // Initialize storage for cron job
  const storage = new DatabaseStorage();

  // Set up daily cleanup cron job at 1:00 AM
  cron.schedule('0 1 * * *', async () => {
    try {
      const result = await storage.cleanupOldBirthdayWishes();
      log(`Daily cleanup at 1 AM: Deleted ${result.deletedCount} old birthday wishes`);
    } catch (error) {
      log(`Error during daily cleanup: ${error}`);
    }
  }, {
    timezone: "Africa/Johannesburg" // South African timezone
  });

  log('Daily birthday cleanup scheduler started (runs at 1:00 AM daily)');

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;

    // Log full error details server-side for debugging
    log(`Error: ${err.message || err}`, 'error');
    if (err.stack) {
      log(err.stack, 'error');
    }

    // In production, send generic messages for 500 errors to avoid leaking internal details
    // For 4xx errors, preserve the specific message (validation, auth, etc.)
    const message = app.get("env") === "production" && status >= 500
      ? "Internal Server Error"
      : (err.message || "Internal Server Error");

    res.status(status).json({ message });
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    const { setupVite } = await import("./vite");
    await setupVite(app, server);
  } else {
    const { serveStatic } = await import("./static");
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

