import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { insertPatientSchema, type InsertPatient } from '@shared/schema';
import { z } from 'zod';
import { apiRequest } from '@/lib/auth';
import { useToast } from '@/hooks/use-toast';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Search, Upload, UserPlus, Eye } from 'lucide-react';

// Extended validation schema for the form
const patientFormSchema = insertPatientSchema.extend({
  firstName: z.string().min(1, 'First name is required'),
  lastName: z.string().min(1, 'Last name is required'),
  phone: z.string().min(7, 'Phone number must be at least 7 characters'),
  idNumber: z.string().min(1, 'ID/Passport number is required'),
  dateOfBirth: z.date().refine(
    (date) => date <= new Date(),
    'Date of birth cannot be in the future'
  ),
});

export default function PatientRegistration() {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const [editingPatient, setEditingPatient] = useState<any | null>(null);
  const [isEditMode, setIsEditMode] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertPatient>({
    resolver: zodResolver(patientFormSchema),
    defaultValues: {
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      dateOfBirth: new Date(),
      gender: 'male' as const,
      idNumber: '',
      address: '',
      medicalAidScheme: '',
      medicalAidNumber: '',
      allergies: '',
    },
  });

  const { data: searchResults } = useQuery({
    queryKey: ['/api/patients/search', searchQuery],
    queryFn: async () => {
      if (!searchQuery.trim()) return [];
      const res = await apiRequest('GET', `/api/patients/search?q=${encodeURIComponent(searchQuery)}`);
      return res.json();
    },
    enabled: searchQuery.length >= 2,
  });

  const createPatientMutation = useMutation({
    mutationFn: async (data: InsertPatient & { photo?: File }) => {
      const formData = new FormData();

      Object.entries(data).forEach(([key, value]) => {
        if (key !== 'photo' && value !== null && value !== undefined) {
          if (value instanceof Date) {
            formData.append(key, value.toISOString());
          } else {
            formData.append(key, value.toString());
          }
        }
      });

      if (selectedFile) {
        formData.append('photo', selectedFile);
      }

      const endpoint = isEditMode ? `/api/patients/${editingPatient.id}` : '/api/patients';
      const method = isEditMode ? 'PUT' : 'POST';
      const res = await apiRequest(method, endpoint, formData);
      return res.json();
    },
    onSuccess: (patient) => {
      toast({
        title: isEditMode ? 'Patient Updated' : 'Patient Registered',
        description: `${patient.firstName} ${patient.lastName} has been successfully ${isEditMode ? 'updated' : 'registered'}.`,
      });
      resetForm();
      // Invalidate all patient-related queries
      queryClient.invalidateQueries({ queryKey: ['/api/patients'] });
      queryClient.invalidateQueries({ queryKey: ['/api/patients/search'] });
      queryClient.invalidateQueries({ predicate: (query) => query.queryKey[0] === '/api/patients/search' });

      // Force refetch search results if there's an active search
      if (searchQuery.length > 2) {
        queryClient.refetchQueries({ queryKey: ['/api/patients/search', searchQuery] });
      }
    },
    onError: (error: any) => {
      let description = error.message;

      // Handle unique constraint violation for ID number
      if (error.message?.includes('unique') || error.message?.includes('duplicate')) {
        description = 'This ID/Passport number is already registered. Please check the number and try again.';
      }

      toast({
        title: isEditMode ? 'Update Failed' : 'Registration Failed',
        description,
        variant: 'destructive',
      });
    },
  });

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = () => {
        setPreviewUrl(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const loadPatientData = (patient: any) => {
    setEditingPatient(patient);
    setIsEditMode(true);

    // Populate the form with patient data
    form.reset({
      firstName: patient.firstName || '',
      lastName: patient.lastName || '',
      email: patient.email || '',
      phone: patient.phone || '',
      dateOfBirth: patient.dateOfBirth ? new Date(patient.dateOfBirth) : new Date(),
      gender: patient.gender || 'male',
      idNumber: patient.idNumber || '',
      address: patient.address || '',
      medicalAidScheme: patient.medicalAidScheme || '',
      medicalAidNumber: patient.medicalAidNumber || '',
      allergies: patient.allergies || '',
    });

    // Set preview URL if patient has a photo
    if (patient.photoUrl) {
      setPreviewUrl(patient.photoUrl);
    }

    // Scroll to form
    setTimeout(() => {
      document.querySelector('#patient-form')?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  const resetForm = () => {
    form.reset({
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      dateOfBirth: new Date(),
      gender: 'male' as const,
      idNumber: '',
      address: '',
      medicalAidScheme: '',
      medicalAidNumber: '',
      allergies: '',
    });
    setSelectedFile(null);
    setPreviewUrl(null);
    setEditingPatient(null);
    setIsEditMode(false);
  };

  const onSubmit = (data: InsertPatient) => {
    createPatientMutation.mutate(data);
  };


  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Patient Registration</h1>
          <p className="text-muted-foreground">Register new patients or update existing patient information</p>
        </div>
      </div>

      {/* Patient Search */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Search className="w-5 h-5" />
            Quick Patient Search
          </CardTitle>
          <CardDescription>
            Search for existing patients to update their information
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex gap-4">
            <Input
              placeholder="Search by name or phone number..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex-1"
              data-testid="input-patient-search"
            />
          </div>

          {searchResults && searchResults.length > 0 && (
            <div className="mt-4 space-y-2">
              <h4 className="font-medium">Search Results:</h4>
              {searchResults.map((patient: any) => (
                <div
                  key={patient.id}
                  className="flex items-center justify-between p-3 border rounded-lg hover:bg-muted/50 cursor-pointer"
                  onClick={() => loadPatientData(patient)}
                  data-testid={`patient-result-${patient.id}`}
                >
                  <div className="flex items-center gap-3">
                    <Avatar>
                      {patient.photoUrl && <AvatarImage src={patient.photoUrl} />}
                      <AvatarFallback>
                        {patient.firstName.charAt(0)}{patient.lastName.charAt(0)}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-medium">{patient.firstName} {patient.lastName}</p>
                      <p className="text-sm text-muted-foreground">{patient.phone}</p>
                    </div>
                  </div>
                  <Button variant="outline" size="sm">
                    Load Data
                  </Button>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Registration Form */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <UserPlus className="w-5 h-5" />
            {isEditMode ? 'Edit Patient Information' : 'Patient Information'}
          </CardTitle>
          <CardDescription>
            {isEditMode ? `Update information for ${editingPatient?.firstName} ${editingPatient?.lastName}` : 'Complete patient profiles with personal details and medical aid information'}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isEditMode && (
            <div className="mb-4 p-3 bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800 rounded-lg">
              <p className="text-sm text-blue-800 dark:text-blue-200">
                <strong>Editing:</strong> {editingPatient.firstName} {editingPatient.lastName}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={resetForm}
                  className="ml-2 h-auto p-1 text-blue-600 hover:text-blue-800"
                >
                  Cancel Edit
                </Button>
              </p>
            </div>
          )}
          <div id="patient-form">
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              {/* Photo Upload */}
              <div className="flex items-center gap-6">
                <div>
                  <Avatar className="w-24 h-24">
                    {previewUrl && <AvatarImage src={previewUrl} />}
                    <AvatarFallback className="text-lg">
                      <Upload className="w-8 h-8" />
                    </AvatarFallback>
                  </Avatar>
                </div>
                <div>
                  <div className="flex gap-2 mb-2">
                    <label htmlFor="photo-upload" className="cursor-pointer">
                      <Button variant="outline" asChild>
                        <span>
                          <Upload className="w-4 h-4 mr-2" />
                          Upload ID/Passport
                        </span>
                      </Button>
                    </label>
                    {previewUrl && (
                      <Button
                        variant="outline"
                        type="button"
                        onClick={() => window.open(previewUrl, '_blank')}
                        data-testid="button-view-id"
                      >
                        <Eye className="w-4 h-4 mr-2" />
                        View ID
                      </Button>
                    )}
                  </div>
                  <input
                    id="photo-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleFileChange}
                    className="hidden"
                    data-testid="input-patient-photo"
                  />
                  <p className="text-sm text-muted-foreground">
                    ID/passport photo
                  </p>
                </div>
              </div>

              {/* Gender Field */}
              <FormField
                control={form.control}
                name="gender"
                render={({ field }) => (
                  <FormItem className="max-w-xs">
                    <FormLabel>Gender *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-gender">
                          <SelectValue placeholder="Select gender" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="male">Male</SelectItem>
                        <SelectItem value="female">Female</SelectItem>
                        <SelectItem value="other">Other</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Personal Information */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="firstName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>First Name *</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-first-name" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="lastName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Last Name *</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-last-name" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input {...field} type="email" value={field.value || ''} data-testid="input-email" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="phone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Phone Number *</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-phone" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="dateOfBirth"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Date of Birth *</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          type="date"
                          value={field.value instanceof Date ? field.value.toISOString().split('T')[0] : ''}
                          onChange={(e) => field.onChange(new Date(e.target.value))}
                          data-testid="input-date-of-birth"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />


                <FormField
                  control={form.control}
                  name="idNumber"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>ID/Passport Number *</FormLabel>
                      <FormControl>
                        <Input {...field} value={field.value || ''} data-testid="input-id-number" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="address"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Address</FormLabel>
                    <FormControl>
                      <Textarea {...field} value={field.value || ''} data-testid="input-address" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="allergies"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Allergies</FormLabel>
                    <FormControl>
                      <Textarea {...field} value={field.value || ''} placeholder="List any known allergies or medications to avoid..." data-testid="input-allergies" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Medical Aid Information */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold">Medical Aid Information</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="medicalAidScheme"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Medical Aid Scheme</FormLabel>
                        <FormControl>
                          <Input {...field} value={field.value || ''} data-testid="input-medical-aid-scheme" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="medicalAidNumber"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Medical Aid Number</FormLabel>
                        <FormControl>
                          <Input {...field} value={field.value || ''} data-testid="input-medical-aid-number" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
              </div>

              <Button 
                type="submit" 
                className="w-full" 
                disabled={createPatientMutation.isPending}
                data-testid="button-register-patient"
              >
{isEditMode 
                  ? (createPatientMutation.isPending ? 'Updating...' : 'Update Patient')
                  : (createPatientMutation.isPending ? 'Registering...' : 'Register Patient')
                }
              </Button>
            </form>
          </Form>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/QueueManagement.tsx.content:
import { useState, useEffect } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/auth';
import { useWebSocket } from '@/lib/websocket';
import { useToast } from '@/hooks/use-toast';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { 
  Users, Clock, Play, Pause, CheckCircle, 
  RotateCcw, AlertCircle, User, Timer
} from 'lucide-react';

export default function QueueManagement() {
  const [selectedDoctor, setSelectedDoctor] = useState<string>('all');
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { lastMessage } = useWebSocket();

  const { data: queue, refetch: refetchQueue } = useQuery({
    queryKey: ['/api/queue', selectedDoctor],
    queryFn: async () => {
      const url = selectedDoctor && selectedDoctor !== 'all' ? `/api/queue?doctorId=${selectedDoctor}` : '/api/queue';
      const res = await apiRequest('GET', url);
      return res.json();
    },
  });

  const { data: doctors } = useQuery({
    queryKey: ['/api/users'],
    queryFn: async () => {
      const res = await apiRequest('GET', '/api/users');
      const users = await res.json();
      return users.filter((user: any) => user.role === 'doctor');
    },
  });

  // Refresh queue when WebSocket message received
  useEffect(() => {
    if (lastMessage?.type === 'queue_update') {
      refetchQueue();
    }
  }, [lastMessage, refetchQueue]);

  const updateQueueStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      const res = await apiRequest('PUT', `/api/queue/${id}/status`, { status });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/queue'] });
    },
    onError: (error) => {
      toast({
        title: 'Status Update Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const removeFromQueueMutation = useMutation({
    mutationFn: async (id: string) => {
      const res = await apiRequest('DELETE', `/api/queue/${id}`);
      return res.json();
    },
    onSuccess: () => {
      toast({
        title: 'Removed from Queue',
        description: 'Patient has been removed from the queue.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/queue'] });
    },
    onError: (error) => {
      toast({
        title: 'Remove Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const updateStatus = (id: string, status: string) => {
    updateQueueStatusMutation.mutate({ id, status });
  };

  const removeFromQueue = (id: string) => {
    removeFromQueueMutation.mutate(id);
  };

  const getStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'waiting': return 'badge-waiting';
      case 'in_progress': return 'badge-in-progress';
      case 'completed': return 'badge-completed';
      default: return 'badge-waiting';
    }
  };

  const getPriorityBadgeClass = (priority: number) => {
    switch (priority) {
      case 2: return 'badge-urgent';
      case 1: return 'bg-orange-100 text-orange-800 border-orange-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getPriorityLabel = (priority: number) => {
    switch (priority) {
      case 2: return 'Urgent';
      case 1: return 'High';
      default: return 'Normal';
    }
  };

  const getWaitTime = (enteredAt: string) => {
    const now = new Date();
    const entered = new Date(enteredAt);
    const diffMs = now.getTime() - entered.getTime();
    const diffMins = Math.floor(diffMs / 60000);
    return diffMins;
  };

  const queueStats = queue ? {
    total: queue.length,
    waiting: queue.filter((item: any) => item.status === 'waiting').length,
    inProgress: queue.filter((item: any) => item.status === 'in_progress').length,
    avgWaitTime: queue.length > 0 ? Math.round(
      queue.reduce((acc: number, item: any) => acc + getWaitTime(item.enteredAt), 0) / queue.length
    ) : 0
  } : { total: 0, waiting: 0, inProgress: 0, avgWaitTime: 0 };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Queue Management</h1>
          <p className="text-muted-foreground">Real-time patient queue monitoring and management</p>
        </div>
        <div className="flex items-center gap-4">
          <Select value={selectedDoctor} onValueChange={setSelectedDoctor}>
            <SelectTrigger className="w-48" data-testid="select-doctor-filter">
              <SelectValue placeholder="Filter by doctor" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Doctors</SelectItem>
              {doctors?.map((doctor: any) => (
                <SelectItem key={doctor.id} value={doctor.id}>
                  {doctor.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Queue Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                <Users className="w-5 h-5 text-blue-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Total in Queue</p>
                <p className="text-2xl font-bold" data-testid="text-total-queue">{queueStats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-yellow-100 rounded-lg flex items-center justify-center">
                <Clock className="w-5 h-5 text-yellow-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Waiting</p>
                <p className="text-2xl font-bold" data-testid="text-waiting-count">{queueStats.waiting}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center">
                <Play className="w-5 h-5 text-green-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">In Progress</p>
                <p className="text-2xl font-bold" data-testid="text-in-progress-count">{queueStats.inProgress}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-purple-100 rounded-lg flex items-center justify-center">
                <Timer className="w-5 h-5 text-purple-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Avg Wait Time</p>
                <p className="text-2xl font-bold" data-testid="text-avg-wait-time">{queueStats.avgWaitTime}m</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Queue List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Users className="w-5 h-5" />
            Current Queue
          </CardTitle>
          <CardDescription>
            Manage patient queue with real-time updates and status tracking
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {queue && queue.length > 0 ? (
              queue.map((queueItem: any, index: number) => (
                <div key={queueItem.id} className="border rounded-lg p-4 hover:bg-muted/50">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                      <div className="text-center min-w-[40px]">
                        <div className="text-xl font-bold text-primary">
                          #{index + 1}
                        </div>
                      </div>

                      <Avatar className="w-12 h-12">
                        {queueItem.patient?.photoUrl && <AvatarImage src={queueItem.patient.photoUrl} />}
                        <AvatarFallback>
                          {queueItem.patient?.firstName?.charAt(0)}{queueItem.patient?.lastName?.charAt(0)}
                        </AvatarFallback>
                      </Avatar>

                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-2">
                          <span className="font-bold text-2xl text-foreground" data-testid={`text-patient-name-${queueItem.id}`}>
                            {queueItem.patient?.firstName} {queueItem.patient?.lastName}
                          </span>
                          <Badge className={`px-2 py-1 text-xs font-medium rounded-full border ${getPriorityBadgeClass(queueItem.priority)}`}>
                            {getPriorityLabel(queueItem.priority)}
                          </Badge>
                        </div>

                        <div className="flex items-center gap-4 text-xs text-muted-foreground">
                          <div className="flex items-center gap-1">
                            <User className="w-3 h-3" />
                            <span>Dr. {queueItem.doctor?.name}</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <Clock className="w-3 h-3" />
                            <span>Waiting {getWaitTime(queueItem.enteredAt)} mins</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <span>
                              Entered: {new Date(queueItem.enteredAt).toLocaleTimeString([], { 
                                hour: '2-digit', 
                                minute: '2-digit' 
                              })}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center gap-3">
                      <Badge className={`px-3 py-1 text-sm font-medium rounded-full border ${getStatusBadgeClass(queueItem.status)}`}>
                        {queueItem.status.replace('_', ' ')}
                      </Badge>

                      <div className="flex gap-2">
                        {queueItem.status === 'waiting' && (
                          <Button
                            size="sm"
                            onClick={() => updateStatus(queueItem.id, 'in_progress')}
                            data-testid={`button-start-${queueItem.id}`}
                          >
                            <Play className="w-4 h-4 mr-1" />
                            Start
                          </Button>
                        )}

                        {queueItem.status === 'in_progress' && (
                          <>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => updateStatus(queueItem.id, 'waiting')}
                              data-testid={`button-pause-${queueItem.id}`}
                            >
                              <Pause className="w-4 h-4 mr-1" />
                              Pause
                            </Button>
                            <Button
                              size="sm"
                              onClick={() => updateStatus(queueItem.id, 'completed')}
                              data-testid={`button-complete-${queueItem.id}`}
                            >
                              <CheckCircle className="w-4 h-4 mr-1" />
                              Complete
                            </Button>
                          </>
                        )}

                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => removeFromQueue(queueItem.id)}
                          data-testid={`button-remove-${queueItem.id}`}
                        >
                          <RotateCcw className="w-4 h-4 mr-1" />
                          Remove
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-12" data-testid="text-empty-queue">
                <Users className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-medium text-foreground mb-2">Queue is Empty</h3>
                <p className="text-muted-foreground">No patients are currently in the queue.</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/UserManagement.tsx.content:
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { insertUserSchema, type InsertUser } from '@shared/schema';
import { apiRequest } from '@/lib/auth';
import { useToast } from '@/hooks/use-toast';
import { formatDate } from '@/lib/utils';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Switch } from '@/components/ui/switch';
import { 
  UserPlus, Users, Mail, Edit, Trash2, 
  Shield, UserCheck, UserX, Settings
} from 'lucide-react';

export default function UserManagement() {
  const [editingUser, setEditingUser] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertUser>({
    resolver: zodResolver(insertUserSchema),
    defaultValues: {
      email: '',
      passwordHash: '',
      name: '',
      role: 'staff',
    },
  });

  const { data: users } = useQuery({
    queryKey: ['/api/users'],
    queryFn: async () => {
      const res = await apiRequest('GET', '/api/users');
      return res.json();
    },
  });

  const createUserMutation = useMutation({
    mutationFn: async (data: InsertUser) => {
      const res = await apiRequest('POST', '/api/users', data);
      return res.json();
    },
    onSuccess: (user) => {
      toast({
        title: 'User Created',
        description: `${user.name} has been successfully created.`,
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
    },
    onError: (error) => {
      toast({
        title: 'Creation Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const updateUserMutation = useMutation({
    mutationFn: async ({ id, ...data }: { id: string } & Partial<InsertUser>) => {
      const res = await apiRequest('PUT', `/api/users/${id}`, data);
      return res.json();
    },
    onSuccess: (user) => {
      toast({
        title: 'User Updated',
        description: `${user.name} has been successfully updated.`,
      });
      setEditingUser(null);
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
    },
    onError: (error) => {
      toast({
        title: 'Update Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const deleteUserMutation = useMutation({
    mutationFn: async (id: string) => {
      const res = await apiRequest('DELETE', `/api/users/${id}`);
      return res.json();
    },
    onSuccess: () => {
      toast({
        title: 'User Deleted',
        description: 'User has been successfully deleted.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
    },
    onError: (error) => {
      toast({
        title: 'Deletion Failed',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const onSubmit = (data: InsertUser) => {
    if (editingUser) {
      updateUserMutation.mutate({ id: editingUser.id, ...data });
    } else {
      createUserMutation.mutate(data);
    }
  };

  const startEditing = (user: any) => {
    setEditingUser(user);
    form.reset({
      email: user.email,
      name: user.name,
      role: user.role,
      passwordHash: '', // Don't pre-fill password
    });
  };

  const cancelEditing = () => {
    setEditingUser(null);
    form.reset();
  };

  const toggleUserStatus = (user: any) => {
    updateUserMutation.mutate({
      id: user.id,
      isActive: !user.isActive
    });
  };

  const deleteUser = (id: string) => {
    if (window.confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
      deleteUserMutation.mutate(id);
    }
  };

  const getRoleBadgeClass = (role: string) => {
    switch (role) {
      case 'admin': return 'bg-red-100 text-red-800 border-red-200';
      case 'doctor': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'staff': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const roleStats = users ? {
    total: users.length,
    admins: users.filter((u: any) => u.role === 'admin').length,
    doctors: users.filter((u: any) => u.role === 'doctor').length,
    staff: users.filter((u: any) => u.role === 'staff').length,
    active: users.filter((u: any) => u.isActive).length,
  } : { total: 0, admins: 0, doctors: 0, staff: 0, active: 0 };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground">User Management</h1>
          <p className="text-muted-foreground">Manage system users, roles, and permissions</p>
        </div>
      </div>

      {/* User Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                <Users className="w-5 h-5 text-blue-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Total Users</p>
                <p className="text-2xl font-bold" data-testid="text-total-users">{roleStats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-red-100 rounded-lg flex items-center justify-center">
                <Shield className="w-5 h-5 text-red-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Admins</p>
                <p className="text-2xl font-bold" data-testid="text-admin-count">{roleStats.admins}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                <UserCheck className="w-5 h-5 text-blue-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Doctors</p>
                <p className="text-2xl font-bold" data-testid="text-doctor-count">{roleStats.doctors}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center">
                <Users className="w-5 h-5 text-green-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Staff</p>
                <p className="text-2xl font-bold" data-testid="text-staff-count">{roleStats.staff}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center">
                <UserCheck className="w-5 h-5 text-green-600" />
              </div>
              <div>
                <p className="text-sm text-muted-foreground">Active</p>
                <p className="text-2xl font-bold" data-testid="text-active-count">{roleStats.active}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* User Form */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <UserPlus className="w-5 h-5" />
              {editingUser ? 'Edit User' : 'Create New User'}
            </CardTitle>
            <CardDescription>
              {editingUser ? 'Update user information and role' : 'Add new users to the system with appropriate roles'}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Full Name *</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-user-name" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email Address *</FormLabel>
                      <FormControl>
                        <Input {...field} type="email" data-testid="input-user-email" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="role"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Role *</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-user-role">
                            <SelectValue placeholder="Select role" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="staff">Staff</SelectItem>
                          <SelectItem value="doctor">Doctor</SelectItem>
                          <SelectItem value="admin">Admin</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="passwordHash"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>
                        {editingUser ? 'New Password (leave blank to keep current)' : 'Password *'}
                      </FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password" 
                          data-testid="input-user-password"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex gap-2">
                  <Button 
                    type="submit" 
                    className="flex-1" 
                    disabled={createUserMutation.isPending || updateUserMutation.isPending}
                    data-testid="button-save-user"
                  >
                    {editingUser ? 'Update User' : 'Create User'}
                  </Button>
                  {editingUser && (
                    <Button 
                      type="button" 
                      variant="outline" 
                      onClick={cancelEditing}
                      data-testid="button-cancel-edit"
                    >
                      Cancel
                    </Button>
                  )}
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>

        {/* Users List */}
        <Card className="lg:col-span-2">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Users className="w-5 h-5" />
              System Users
            </CardTitle>
            <CardDescription>
              Manage existing users, roles, and account status
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {users && users.length > 0 ? (
                users.map((user: any) => (
                  <div key={user.id} className="border rounded-lg p-4 hover:bg-muted/50">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-4">
                        <Avatar className="w-12 h-12">
                          <AvatarFallback>
                            {user.name.split(' ').map((n: string) => n.charAt(0)).join('').toUpperCase()}
                          </AvatarFallback>
                        </Avatar>

                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <span className="font-semibold text-lg" data-testid={`text-user-name-${user.id}`}>
                              {user.name}
                            </span>
                            {!user.isActive && (
                              <Badge variant="secondary" className="text-xs">
                                Inactive
                              </Badge>
                            )}
                          </div>

                          <div className="flex items-center gap-4 text-sm text-muted-foreground">
                            <div className="flex items-center gap-1">
                              <Mail className="w-4 h-4" />
                              <span>{user.email}</span>
                            </div>
                            <Badge className={`px-2 py-1 text-xs font-medium rounded-full border ${getRoleBadgeClass(user.role)}`}>
                              {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                            </Badge>
                          </div>

                          <div className="text-xs text-muted-foreground mt-1">
                            Created: {formatDate(user.createdAt)}
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center gap-3">
                        <div className="flex items-center gap-2">
                          <span className="text-sm">Active</span>
                          <Switch
                            checked={user.isActive}
                            onCheckedChange={() => toggleUserStatus(user)}
                            data-testid={`switch-user-status-${user.id}`}
                          />
                        </div>

                        <div className="flex gap-2">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => startEditing(user)}
                            data-testid={`button-edit-${user.id}`}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>

                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => deleteUser(user.id)}
                            data-testid={`button-delete-${user.id}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-12" data-testid="text-no-users">
                  <Users className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-foreground mb-2">No Users Found</h3>
                  <p className="text-muted-foreground">Create your first user to get started.</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Role Permissions Info */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Settings className="w-5 h-5" />
            Role Permissions
          </CardTitle>
          <CardDescription>
            Understanding user roles and their access levels
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="space-y-3">
              <div className="flex items-center gap-2">
                <Badge className="bg-green-100 text-green-800 border-green-200">
                  Staff
                </Badge>
                <span className="font-medium">Staff Members</span>
              </div>
              <ul className="text-sm text-muted-foreground space-y-1 ml-4">
                <li>‚Ä¢ Patient registration and management</li>
                <li>‚Ä¢ Appointment scheduling</li>
                <li>‚Ä¢ Patient check-in</li>
                <li>‚Ä¢ Queue management</li>
                <li>‚Ä¢ Basic reporting</li>
              </ul>
            </div>

            <div className="space-y-3">
              <div className="flex items-center gap-2">
                <Badge className="bg-blue-100 text-blue-800 border-blue-200">
                  Doctor
                </Badge>
                <span className="font-medium">Medical Doctors</span>
              </div>
              <ul className="text-sm text-muted-foreground space-y-1 ml-4">
                <li>‚Ä¢ All staff permissions</li>
                <li>‚Ä¢ Patient consultations</li>
                <li>‚Ä¢ Medical records access</li>
                <li>‚Ä¢ Prescription management</li>
                <li>‚Ä¢ Queue management for assigned patients</li>
              </ul>
            </div>

            <div className="space-y-3">
              <div className="flex items-center gap-2">
                <Badge className="bg-red-100 text-red-800 border-red-200">
                  Admin
                </Badge>
                <span className="font-medium">System Administrators</span>
              </div>
              <ul className="text-sm text-muted-foreground space-y-1 ml-4">
                <li>‚Ä¢ All system permissions</li>
                <li>‚Ä¢ User management</li>
                <li>‚Ä¢ Business insights and analytics</li>
                <li>‚Ä¢ System administration</li>
                <li>‚Ä¢ Financial reporting</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

