import { createContext, useContext, useEffect, useState, ReactNode } from 'react';

type Theme = 'light' | 'dark';

interface ThemeContextType {
  theme: Theme;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

interface ThemeProviderProps {
  children: ReactNode;
}

export function ThemeProvider({ children }: ThemeProviderProps) {
  const [theme, setTheme] = useState<Theme>(() => {
    // Check localStorage first, then system preference, default to light
    const stored = localStorage.getItem('theme');
    if (stored === 'light' || stored === 'dark') {
      return stored;
    }

    // Check system preference
    if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
      return 'dark';
    }

    return 'light';
  });

  useEffect(() => {
    // Update document class and localStorage
    const root = document.documentElement;

    if (theme === 'dark') {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }

    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
}

client/src/pages/CustomerRelations.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { 
  Gift, Heart, Users, Send, Loader2, 
  Calendar, MessageSquare, Megaphone 
} from 'lucide-react';

interface Patient {
  id: string;
  firstName: string;
  lastName: string;
  phone: string;
  email?: string;
  dateOfBirth: string;
}

interface BirthdayWish {
  patientId: string;
  message: string;
  status: 'sent' | 'pending';
  sentAt?: string;
}


export default function CustomerRelations() {
  const { toast } = useToast();
  const [birthdayCustomMessage, setBirthdayCustomMessage] = useState<string>('');
  const [broadcastMessage, setBroadcastMessage] = useState<string>('');
  const [processingSendId, setProcessingSendId] = useState<string | null>(null);
  const [selectedBirthdayPatient, setSelectedBirthdayPatient] = useState<string | null>(null);

  // Get today's birthday patients
  const { data: birthdayPatients, isLoading: loadingBirthdays } = useQuery({
    queryKey: ['/api/patients/birthdays'],
    queryFn: async () => {
      const res = await apiRequest('GET', '/api/patients/birthdays');
      return res.json();
    },
    refetchInterval: 60000, // Refresh every minute to catch midnight updates
  });


  // Get birthday wishes sent today
  const { data: sentWishes } = useQuery({
    queryKey: ['/api/birthday-wishes'],
    queryFn: async () => {
      const res = await apiRequest('GET', '/api/birthday-wishes');
      return res.json();
    },
  });

  // Send birthday wish mutation
  const sendBirthdayWishMutation = useMutation({
    mutationFn: async ({ patientId, customMessage }: { patientId: string; customMessage?: string }) => {
      // Set the current processing ID
      setProcessingSendId(patientId);

      const response = await apiRequest('POST', '/api/send-birthday-wish', {
        patientId,
        customMessage: customMessage?.trim() || undefined
      });
      return { ...await response.json(), patientId };
    },
    onSuccess: (data) => {
      // Clear processing ID
      setProcessingSendId(null);

      toast({
        title: "Birthday Wish Sent!",
        description: `Message sent successfully: "${data.message}"`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/birthday-wishes'] });
    },
    onError: (error, variables) => {
      // Clear processing ID
      setProcessingSendId(null);

      toast({
        title: "Error",
        description: "Failed to send birthday wish. Please try again.",
        variant: "destructive"
      });
    }
  });

  // Send broadcast message mutation
  const sendBroadcastMutation = useMutation({
    mutationFn: async ({ message }: { message: string }) => {
      const response = await apiRequest('POST', '/api/send-broadcast', {
        message
      });
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Broadcast Sent!",
        description: "Message sent successfully to n8n workflow",
      });
      setBroadcastMessage('');
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to send broadcast message. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleSendBroadcast = () => {
    if (!broadcastMessage.trim()) {
      toast({
        title: "Error", 
        description: "Please enter a message to broadcast.",
        variant: "destructive"
      });
      return;
    }

    sendBroadcastMutation.mutate({
      message: broadcastMessage.trim()
    });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'long',
      day: 'numeric'
    });
  };

  const isWishSent = (patientId: string) => {
    return sentWishes?.some((wish: BirthdayWish) => wish.patientId === patientId);
  };

  const isProcessingSend = (patientId: string) => {
    return processingSendId === patientId;
  };

  const isAnyProcessing = () => {
    return processingSendId !== null;
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Customer Relations</h1>
          <p className="text-muted-foreground">Manage patient communications and engagement</p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Birthday Wishes Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Gift className="h-5 w-5 text-pink-500" />
              Birthday Wishes
            </CardTitle>
            <CardDescription>
              Send personalized birthday messages to patients celebrating today
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {/* Custom Message Input */}
              <div className="space-y-2">
                <Label htmlFor="birthday-message">Custom Birthday Message (Optional)</Label>
                <Textarea
                  id="birthday-message"
                  placeholder="Enter a custom birthday message, or leave blank to use the default message..."
                  value={birthdayCustomMessage}
                  onChange={(e) => setBirthdayCustomMessage(e.target.value)}
                  rows={3}
                  data-testid="textarea-birthday-custom-message"
                />
                <div className="text-sm text-muted-foreground bg-muted/50 p-3 rounded-lg border-l-4 border-blue-500">
                  <p className="font-medium text-foreground mb-1">Default Birthday Message:</p>
                  <p className="italic">"Happy Birthday [Name]! üéâ Wishing you a wonderful year ahead..."</p>
                  <p className="text-xs mt-1">Leave the custom message field empty to use this default template.</p>
                </div>
              </div>

              {loadingBirthdays ? (
                <div className="flex items-center justify-center py-8">
                  <Loader2 className="h-6 w-6 animate-spin" />
                  <span className="ml-2">Loading birthdays...</span>
                </div>
              ) : birthdayPatients?.length > 0 ? (
                <div className="space-y-4">
                  {/* Patient Selection List */}
                  <div className="space-y-3">
                    {birthdayPatients.map((patient: Patient) => (
                      <div key={patient.id} className={`flex items-center justify-between p-3 border rounded-lg ${selectedBirthdayPatient === patient.id ? 'border-blue-500 bg-blue-50/50' : ''}`}>
                        <div className="flex items-center gap-3">
                          <input
                            type="radio"
                            id={`radio-birthday-${patient.id}`}
                            name="birthday-patient"
                            checked={selectedBirthdayPatient === patient.id}
                            onChange={() => setSelectedBirthdayPatient(patient.id)}
                            disabled={isWishSent(patient.id) || isAnyProcessing()}
                            className="h-4 w-4 text-blue-600"
                            data-testid={`radio-birthday-patient-${patient.id}`}
                          />
                          <Calendar className="h-5 w-5 text-blue-500" />
                          <div>
                            <p className="font-medium" data-testid={`text-birthday-patient-${patient.id}`}>
                              {patient.firstName} {patient.lastName}
                            </p>
                            <p className="text-sm text-muted-foreground">
                              {formatDate(patient.dateOfBirth)} ‚Ä¢ {patient.phone}
                            </p>
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          {isWishSent(patient.id) ? (
                            <Badge variant="secondary" data-testid={`badge-sent-${patient.id}`}>
                              <MessageSquare className="h-3 w-3 mr-1" />
                              Sent
                            </Badge>
                          ) : isProcessingSend(patient.id) ? (
                            <Badge variant="outline" data-testid={`badge-sending-${patient.id}`}>
                              <Loader2 className="h-3 w-3 mr-1 animate-spin" />
                              Sending...
                            </Badge>
                          ) : null}
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Send Button */}
                  <div className="flex justify-between items-center pt-3 border-t">
                    <div className="text-sm text-muted-foreground">
                      {selectedBirthdayPatient ? 
                        `Selected: ${birthdayPatients.find((p: Patient) => p.id === selectedBirthdayPatient)?.firstName} ${birthdayPatients.find((p: Patient) => p.id === selectedBirthdayPatient)?.lastName}` 
                        : 'No patient selected'
                      }
                    </div>
                    <Button 
                      onClick={() => {
                        if (selectedBirthdayPatient) {
                          sendBirthdayWishMutation.mutate({ 
                            patientId: selectedBirthdayPatient, 
                            customMessage: birthdayCustomMessage 
                          });
                        }
                      }}
                      disabled={
                        !selectedBirthdayPatient || 
                        isAnyProcessing() ||
                        (selectedBirthdayPatient && isWishSent(selectedBirthdayPatient))
                      }
                      data-testid="button-send-birthday-selected"
                    >
                      {isAnyProcessing() ? (
                        <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      ) : (
                        <Send className="h-4 w-4 mr-2" />
                      )}
                      {birthdayCustomMessage.trim() ? 'Send Custom Message' : 'Send Default Message'}
                    </Button>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8">
                  <Gift className="h-12 w-12 text-muted-foreground mx-auto mb-2" />
                  <p className="text-muted-foreground">No birthdays today</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Broadcast Message Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Megaphone className="h-5 w-5 text-blue-500" />
              Broadcast Message
            </CardTitle>
            <CardDescription>
              Send messages to all your active users. This message will be sent in batches in the background.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {/* Message Input */}
              <div>
                <Label htmlFor="broadcast-message">Message</Label>
                <Textarea
                  id="broadcast-message"
                  placeholder="Enter your message to broadcast..."
                  value={broadcastMessage}
                  onChange={(e) => setBroadcastMessage(e.target.value)}
                  rows={4}
                  data-testid="textarea-broadcast-message"
                />
                <div className="text-sm text-muted-foreground mt-2">
                  This message will be sent to all your active users in batches through the background workflow.
                </div>
              </div>

              {/* Send Button */}
              <Button 
                onClick={handleSendBroadcast}
                disabled={sendBroadcastMutation.isPending || !broadcastMessage.trim()}
                className="w-full"
                data-testid="button-send-broadcast"
              >
                {sendBroadcastMutation.isPending ? (
                  <Loader2 className="h-4 w-4 animate-spin mr-2" />
                ) : (
                  <Megaphone className="h-4 w-4 mr-2" />
                )}
                Send Broadcast Message
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Broadcast Message Preview */}
      {broadcastMessage && (
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Message Preview</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="bg-muted p-4 rounded-lg">
              <p className="text-sm">{broadcastMessage}</p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/PublicPatientRegistrati.content:
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { AlertCircle, CheckCircle, UserPlus } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useState } from "react";

const patientRegistrationSchema = z.object({
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  phone: z.string().min(10, "Valid phone number is required"),
  email: z.string().min(1, "Email is required").email("Valid email is required"),
  dateOfBirth: z.string().min(1, "Date of birth is required"),
  gender: z.enum(["male", "female", "other"], {
    required_error: "Please select a gender",
  }),
  idNumber: z.string().min(1, "ID number is required"),
  address: z.string().optional(),
  medicalAidScheme: z.string().optional(),
  medicalAidNumber: z.string().optional(),
  allergies: z.string().optional(),
});

type PatientRegistrationForm = z.infer<typeof patientRegistrationSchema>;

export default function PublicPatientRegistration() {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitResult, setSubmitResult] = useState<{
    success: boolean;
    message: string;
    patientId?: string;
  } | null>(null);

  const form = useForm<PatientRegistrationForm>({
    resolver: zodResolver(patientRegistrationSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      phone: "",
      email: "",
      dateOfBirth: "",
      gender: undefined,
      idNumber: "",
      address: "",
      medicalAidScheme: "",
      medicalAidNumber: "",
      allergies: "",
    },
  });

  const onSubmit = async (data: PatientRegistrationForm) => {
    setIsSubmitting(true);
    setSubmitResult(null);

    try {
      const response = await fetch('/api/public/patient/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      setSubmitResult({
        success: result.success,
        message: result.message,
        patientId: result.patientId,
      });

      if (result.success) {
        form.reset();
      }
    } catch (error) {
      setSubmitResult({
        success: false,
        message: "Network error. Please try again.",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8 px-4">
      <div className="max-w-2xl mx-auto">
        <Card>
          <CardHeader className="text-center">
            <div className="mx-auto w-12 h-12 bg-blue-100 dark:bg-blue-900 rounded-full flex items-center justify-center mb-4">
              <UserPlus className="h-6 w-6 text-blue-600 dark:text-blue-400" />
            </div>
            <CardTitle className="text-2xl font-bold">Patient Registration</CardTitle>
            <CardDescription>
              Please fill out this form to register as a new patient at our clinic.
              All fields marked with * are required.
            </CardDescription>
          </CardHeader>
          <CardContent>
            {submitResult && (
              <Alert className={`mb-6 ${submitResult.success ? 'border-green-200 bg-green-50 dark:bg-green-950' : 'border-red-200 bg-red-50 dark:bg-red-950'}`}>
                {submitResult.success ? (
                  <CheckCircle className="h-4 w-4 text-green-600" />
                ) : (
                  <AlertCircle className="h-4 w-4 text-red-600" />
                )}
                <AlertDescription className={submitResult.success ? 'text-green-800 dark:text-green-200' : 'text-red-800 dark:text-red-200'}>
                  {submitResult.message}
                  {submitResult.success && submitResult.patientId && (
                    <div className="mt-2 font-medium">
                      Your Patient ID: {submitResult.patientId}
                    </div>
                  )}
                </AlertDescription>
              </Alert>
            )}

            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                {/* Personal Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                    Personal Information
                  </h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="firstName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>First Name *</FormLabel>
                          <FormControl>
                            <Input data-testid="input-firstname" placeholder="Enter your first name" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="lastName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Last Name *</FormLabel>
                          <FormControl>
                            <Input data-testid="input-lastname" placeholder="Enter your last name" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="phone"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Phone Number *</FormLabel>
                          <FormControl>
                            <Input 
                              data-testid="input-phone" 
                              placeholder="+27123456789" 
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="email"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Email *</FormLabel>
                          <FormControl>
                            <Input 
                              data-testid="input-email" 
                              type="email" 
                              placeholder="your.email@example.com" 
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="dateOfBirth"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Date of Birth *</FormLabel>
                          <FormControl>
                            <Input 
                              data-testid="input-dob" 
                              type="date" 
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="gender"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Gender *</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-gender">
                                <SelectValue placeholder="Select gender" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="male">Male</SelectItem>
                              <SelectItem value="female">Female</SelectItem>
                              <SelectItem value="other">Other</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="idNumber"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>ID Number *</FormLabel>
                        <FormControl>
                          <Input 
                            data-testid="input-idnumber" 
                            placeholder="Enter your ID number" 
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="address"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Address</FormLabel>
                        <FormControl>
                          <Textarea 
                            data-testid="input-address" 
                            placeholder="Enter your full address" 
                            rows={3}
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Medical Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                    Medical Aid Information (Optional)
                  </h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="medicalAidScheme"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Medical Aid Scheme</FormLabel>
                          <FormControl>
                            <Input 
                              data-testid="input-medicalaid-scheme" 
                              placeholder="e.g., Discovery Health" 
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="medicalAidNumber"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Medical Aid Number</FormLabel>
                          <FormControl>
                            <Input 
                              data-testid="input-medicalaid-number" 
                              placeholder="Enter your medical aid number" 
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="allergies"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Known Allergies</FormLabel>
                        <FormControl>
                          <Textarea 
                            data-testid="input-allergies" 
                            placeholder="List any known allergies (medications, foods, etc.)" 
                            rows={3}
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <Button 
                  type="submit" 
                  className="w-full" 
                  disabled={isSubmitting}
                  data-testid="button-register"
                >
                  {isSubmitting ? "Registering..." : "Register as Patient"}
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

